import React, { Component } from "react";
import Add  from "./components/Add"
import Current  from "./components/Current"
import Search from "./components/Search"


class ToDoContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {allTodos : [], filter : '', id : 0};
  }

  addTodo = (payload) => {
    this.setState((state, props) => ({
      allTodos : state.allTodos.concat([{
        payload : payload,
        id : state.id
      }]),
      id : state.id + 1
    }));
  }

  deleteTodo = (id) => {
    this.setState((state, props) => ({
      allTodos : state.allTodos.filter(item => item.id !== id)
    }));
  }

  searchTodo = (criteria) => {
    this.setState({
      filter : criteria
    });
  }

  render() {

    const { allTodos, fiter } = this.state;
    const displayTodos = allTodos.filter(item => item.payload.match(filter));

    if (allTodos.length = 0) {
      return (
        <div>
          No todos added.
        </div>
      );
    }
    return (
      <div>
        <div>
          <Add addTodo={this.addTodo} />
        </div>
        <div>
          <Current todos={displayTodos} deleteTodo={this.deleteTodo} />
        </div>
        <div>
          <Search searchTodo={this.searchTodo}/>
        </div>
      </div>
    );
  }
}

export default ToDoContainer;
