import React, { Component } from "react";

import { AddButton, InputAdd } from './styles';
import { Div } from "./../../styles"


class Add extends Component {
    constructor(props) {
      super(props); 
      this.state = {value : ''};
    }

    handleChange = (event) => {
      this.setState({value: event.target.value});
    }

    handleSubmit = (event) => {
        event.preventDefault();
        this.props.addTodo(this.state.value);
        this.setState({value: ''});
    }

    render () {
      return (
        <Div >
          <InputAdd type="text" value={this.state.value} onChange={this.handleChange}/>
          <AddButton onClick={this.handleSubmit}> Add </AddButton>
        </Div>
      );
    }
  }

export default Add;
