import React, { Component } from "react";
import Add  from "./components/Add"
import Current  from "./components/Current"
import Search from "./components/Search"
import {Container, H1} from "./styles"


class ToDoContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {allTodos : [], filter : '', id : 0};
  }

  addTodo = (payload) => {
    this.setState((state, props) => ({
      allTodos : [...state.allTodos, { payload, id : state.id }],
      id : state.id + 1
    }));
  }

  deleteTodo = (id) => {
    this.setState((state, props) => ({
      allTodos : state.allTodos.filter(item => item.id !== id)
    }));
  }

  searchTodo = (criteria) => {
    this.setState({
      filter : criteria
    });
  }

  render() {

    const { allTodos, filter } = this.state;
    const displayTodos = allTodos.filter(item => item.payload.match(filter));


    let content;

    if (allTodos.length === 0) {
      content = <p>No todos added.</p>;
    }
    else if (displayTodos.length === 0) {
      content = <p>No todos found.</p>;
    }
    else {
      content = <Current todos={displayTodos} deleteTodo={this.deleteTodo} />;
    }
    return (
      <Container flexDirection="row-reverse" flexWrap="nowrap" justifyContent="center" alignItems="center" alignContent="center">
        <Container flexBasis="1%">
          <H1>Current Todos:</H1>
          {content}
        </Container>
        <Container flexBasis="1%" alignSelf="center" flexDirection="column">
            <Add addTodo={this.addTodo} />
            <Search searchTodo={this.searchTodo}/>
        </Container>
      </Container>
    );
  }
}

export default ToDoContainer;
